import React from "react"

import { Icon } from 'components/Icon'

export const Button = ({ type, state, text, leftIcon, rightIcon, handleClick }) => {
    const renderLeftIcon = leftIcon ? (
        <Icon
            name={ leftIcon }
            size='30'
        ></Icon>
    ) : null
    const renderRightIcon = rightIcon ? (
        <Icon
            name={ rightIcon }
            size='30'
        ></Icon>
    ) : null

    const isDisabled = false

    return (
        <button
            className={type + ' button ' + state}
            onClick={handleClick}
            disabled={isDisabled}
        >
            { renderLeftIcon }
            <span>{text}</span>
            { renderRightIcon }
        </button>
    );
}