import React from "react"

import { Icon } from 'components/Icon'

export const Button = ({
    type,
    text,
    leftIcon,
    rightIcon,
    handleClick,
    isDisabled
}) => {
    const renderLeftIcon = leftIcon ? (
        <Icon
            name={ leftIcon }
            size='30'
        ></Icon>
    ) : null
    const renderRightIcon = rightIcon ? (
        <Icon
            name={ rightIcon }
            size='30'
        ></Icon>
    ) : null

    return (
        <button
            className={type + ' button'}
            onClick={handleClick}
            disabled={isDisabled}
        >
            { renderLeftIcon }
            <span>{text}</span>
            { renderRightIcon }
        </button>
    );
}