import React from 'react'
import { Controller } from 'react-hook-form'

import { MCTLayout } from 'containers/MCTLayout'
import { WishCard } from 'containers/WishCard'
import { Button } from 'components/Button'
import { IconButton } from 'components/IconButton'


export const CardSelect = ({ control, name, options, ...rest }) => {
    return (
        <Controller
            control={control}
            name={name}
            render={({ field: { value, onChange }}) => (
                // value: [...wishIds]
                <div className='card-select'>
                    <div className='line-container'>
                        <span>{ value.length
                        ? `Выбрано желаний: ${value.length}`                           
                        : 'Кликните на карточку, чтобы добавить желание в вишлист'
                        }</span>
                        <IconButton
                            icon='clear'
                            onClick={() => onChange([])}
                        />
                        <Button
                            text='Выделить все'
                            onClick={() => onChange(options.map(option => option.id))}
                        />
                    </div>
                    <MCTLayout
                        Component={WishCard}
                        isInput
                        value={value}
                        onChange={onChange}
                    />
                </div>
            )}
        />
    );
}