import React from 'react'
import { Controller } from 'react-hook-form'

import { WishCard } from 'containers/WishCard'
import { Button } from 'components/Button'
import { IconButton } from 'components/IconButton'
import { MultiColumnLayout } from 'containers/MultiColumnLayout'


export const CardSelect = ({ control, name, options }) => {
    const Spacer = () => <div style={{flex: 1}}/>
    return (
        <Controller
            control={control}
            name={name}
            render={({ field: { value, onChange }}) => (
                // value: [...wishIds]
                <div className='card-select'>
                    <div className='line-container'>
                        <span>{ value.length
                        ? `Выбрано желаний: ${value.length}`                           
                        : 'Кликните на карточку, чтобы добавить желание в вишлист'
                        }</span>
                        <Spacer/>
                        <IconButton
                            icon='clear'
                            onClick={(e) => {
                                e.preventDefault();
                                onChange([]);
                            }}
                        />
                        <Button
                            text='Выделить все'
                            onClick={(e) => {
                                e.preventDefault();
                                onChange(options.map(option => option.id));
                            }}
                            round
                            disabled={value.length === options.length}
                        />
                    </div>
                    <MultiColumnLayout
                        Card={WishCard}
                        data={options}
                        isInput
                        value={value}
                        onChange={onChange}
                    />
                </div>
            )}
        />
    );
}