import React from 'react'

import { Icon } from 'components/Icon'

export const IconButton = ({ icon, size, func, style, className, disabled }) => {
    const styles = {
        width: `${size || 4}rem`,
        height: `${size || 4}rem`,
        ...style
    }
    return (
        <button
            className={ 'icon-button ' + className }
            onClick={ func }
            style={ styles && styles }
            disabled={ disabled }
        >
            <Icon
                name={ icon }
                size={ size && size }
            />
        </button>
    );
}