import React from 'react'

import { Icon } from 'components/Icon'

export const IconButton = ({ icon, size, func }) => {
    const styles = {
        width: `${size}px`,
        height: `${size}px`
    }
    return (
        <button className='icon-button' onClick={func} style={styles}>
            <Icon name={icon} size={size} />
        </button>
    );
}