import React from 'react'

import { Icon } from 'components/Icon'

export const IconButton = ({ icon, size, style, className, ...rest }) => {
    const styles = {
        width: `${size || 4}rem`,
        height: `${size || 4}rem`,
        ...style
    }
    return (
        <button
            className={ 'icon-button' + (className ? ' ' + className : '') }
            style={ styles }
            {...rest}
        >
            <Icon
                name={ icon }
                size={ size && size }
            />
        </button>
    );
}