import React from 'react'

import { Icon } from 'components/Icon'

export const IconButton = ({ icon, size, func, style, className, disabled }) => {
    const styles = {
        width: `${size || 44}px`,
        height: `${size || 44}px`,
        ...style
    }
    return (
        <button
            className={'icon-button ' + className}
            onClick={func}
            style={styles}
            disabled={disabled}
        >
            <Icon
                name={icon}
                size={size}
            />
        </button>
    );
}