import React, { useEffect } from 'react'
import { NavLink } from 'react-router-dom'
import { useDispatch } from 'react-redux'

import { Icon } from 'components/Icon'
import { Highlighter } from 'components/Highlighter'
import { setHighlighterCoordsInModeToggle } from 'store/highlighterSlice'

export const ModeToggle = ({ isMyWishesTab, isItemsMode }) => {
    const Button = ({ to, icon, className }) => {
        return (
            <NavLink to={ to } className={className + ` toggle-button`}>
                <Icon name={ icon }/>
            </NavLink>
        );
    }

    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(setHighlighterCoordsInModeToggle())
    })

    return (
        <div className='items-lists-toggle'>
            <div className='container'>
                <Highlighter location='mode-toggle'/>
                <Button
                    icon='present'
                    className={ isItemsMode ? 'active' : ''}
                    to={ isMyWishesTab
                        ? '/my-wishes/items/actual'
                        : '/my-invites/items/reserved'
                    }
                />
                <Button
                    icon='wishlist'
                    className={ isItemsMode ? '' : 'active'}
                    to={ isMyWishesTab
                        ? '/my-wishes/lists/'
                        : '/my-invites/lists/'
                    }
                />
            </div>
        </div>
    );
}