import React, { Children } from 'react'
import { useDispatch, useSelector } from 'react-redux'

import { Button } from 'components/Button'
import { NavLink } from 'react-router-dom'



export const SideBar = () => {
    const dispatch = useDispatch();

    const Option = ({ children, path }) => {
        return(
            <NavLink to={ path } className={({isActive}) => isActive ? 'side-nav active' : 'side-nav'}>
                { children }
            </NavLink>
        )
    }

    return (
        <div className='sidebar'>
            <Option path='/my-wishes/all'>Все</Option>
            <Option path='/my-wishes/actual'>Актуальные</Option>
            <Option path='/my-wishes/done'>Исполненные</Option>
        </div>
    );
}