import React from 'react'
import { useDispatch, useSelector } from 'react-redux'

import { Button } from 'components/Button'
import { addColumn, removeColumn } from 'store/wishColumnSlice'



export const SideBar = () => {
    const columns = useSelector(state => state.wishColumns.wishColumns)
    const dispatch = useDispatch();
    const plusColumn = () => dispatch(addColumn());
    const deleteColumn = () => dispatch(removeColumn());

    return (
        <div className='sidebar'>
            <Button text={`Добавить колонку`} handleClick={plusColumn}/>
            <Button text={`Удалить колонку`} handleClick={deleteColumn}/>
        </div>
    );
}