import React from 'react'

import { generateId } from 'getters/idGen'
import { Icon } from 'components/Icon'
import { TextLabel } from 'components/TextLabel'

export const TextInput = ({ register, name, label, labelWidth, multiline, required, ...props}) => {
    const id = generateId();
    return (
        <div className='text-input'>
            <TextLabel
                htmlFor={id}
                text={label}
                style={{ width: labelWidth }}
                required={required}
            />
            { multiline
            ?   <>
                    <textarea
                        id={id}
                        {...register(name)}
                        {...props}
                    />
                    <Icon name='resizer' size='22'/>
                </>
            :   <input
                    id={id}
                    type='text'
                    {...register(name)}
                    {...props}
                />
            }
        </div>
    );
}