import React from 'react'

import { Icon } from 'components/Icon'

export const TextLabel = ({ htmlFor, text, required, ...rest }) => {
    return (
        <label
            htmlFor={ htmlFor }
            className='text'
            {...rest}
        >
            <span>{ text }</span>
            { required
            ?   <Icon
                    name='star'
                    size='22'
                />
            :   <span>(optional)</span>
            }
        </label>
    );
}