import React from 'react'

import { getUserByUserName } from 'getters'

export const User = ({
    userName,
    picSize,
    isPicOnly
}) => {
    const u = getUserByUserName(userName)
    const sizeStyles = picSize
        ? { width: `${picSize}px`, height: `${picSize}px` }
        : { width: '44px', height: '44px' }

    const nameString = isPicOnly
        ? null
        : <span className='name'>@{ userName }</span>

    return (
        <div className='user'>
            <img src={ u.avatar } style={ sizeStyles }/>
            { nameString }
        </div>
    );
}