import React from 'react'

import { Button } from 'components/Button';

export const WishCard = ({ wish, className }) => {
    const w = wish;
    const currency = w.currency==='rouble' ? '₽'
                   : w.currency==='yuan' ? `Ұ`
                   : w.currency==='usd' ? '$'
                   : w.currency==='euro' ? '€'
                   : "";
    const price = w.price === null ? ''
                : (w.circa && '≈ ') + w.price + ' ' + currency
    
    return (
        <div className={className + ' wish-card'} key={ w.id }>
            <img src={ w.image }></img>
            <div className='wish-content'>
                <span className='wish-name'>{ w.name }</span>
                <p className='wish-description'>{ w.description }</p>
                <div className='price-block'>
                    <span className='price'>{price}</span>
                    <a href={ w.external }>Подробнее</a>
                </div>
            </div>
            <Button
                text="Исполнить желание"
                type="primary"
                state={ w.state }
            />
            <div className='stars'></div>
        </div>
    )
}