import React from 'react'
import { NavLink } from 'react-router-dom'
import { Outlet } from 'react-router'

import { ItemsListsToggle } from 'components/ItemsListsToggle'



export const NavBar = () => {
    const Option = ({ to, children }) => {
        return (
            <div><NavLink
                to={ to }
                className='nav-option'
            >
                { children }
            </NavLink>
            </div>
        );
    }
    const Slash = () => <div className='nav-option'>/</div>

    return (
        <div className='app-body'>
            <div className='content-header'>
                <div className='nav-bar'>
                    <ItemsListsToggle/>
                    <Option to='/my-wishes/items/actual'>Што желаеш роскажу</Option>
                    <Slash/>
                </div>
            </div>
            <div className='work-space'>
                <Outlet/>
            </div>
        </div>
    )
}