import { useSelector } from 'react-redux'

export const getWishById = ( id ) => {
    const wishes = useSelector(state => state.wishes.all);
    console.log('array of wishes is: ', wishes)
    return wishes.find( wish => wish.id === id )
}

export const getUserByUserName = ( userName ) => {
    const users = useSelector(state => state.users.all)
    console.log('array of users is: ', users)
    return users.find( user => user.userName === userName )
}

export const getWishlistById = ( id ) => {
    const wishlists = useSelector(state => state.wishlists.all)
    console.log('array of wishlists is: ', wishlists)
    return wishlists.find( wishlist => wishlist.id === id )
}