import { createSlice } from '@reduxjs/toolkit'

import { wishes } from 'api/mockWishes'

const wishesSlice = createSlice({
    name: 'wishes',
    initialState: {
        wishes: []
    },
    reducers: {
        fetchWishes(state) {
            state.wishes = [...wishes];
        }
    }
});

export const { fetchWishes } = wishesSlice.actions;

export default wishesSlice.reducer;